    foreColor.blue = 0;                                 // Âõ†ËÄå(65535,0,0)Ë°®Á§∫Á∫¢Ëâ≤
    foreColor.green = 0;
    XAllocColor(dsp, screen_colormap, &foreColor);

    //-- ÊòæÁ§∫winËøô‰∏™Á™óÂè£‰∏∫ÊúÄÂâç
